mapper.select:
SELECT

,

,

FROM
weighing_bill wb
INNER JOIN
weighing_statement ws
ON
wb.id =
ws.weighing_bill_id
LEFT JOIN
( SELECT id,weighing_bill_id, operator_id, operation_type, operation_type_name,operator_user_name,
operator_name, operation_time
FROM weighing_bill_operation_record WHERE operation_type = 4 ) wbor ON wb.id =
wbor.weighing_bill_id
WHERE 1 = 1

	AND ws.state IN
	
		#{item}
	


	AND wb.seller_card_no = #{sellerCardNo}


	AND wb.buyer_card_no = #{buyerCardNo}


	AND wb.state=#{state}


	AND wb.trade_type_id=#{tradeTypeId}


	AND wb.goods_name IN
	
		#{goods}
	


	AND wb.buyer_id = #{buyerId}


	AND wb.seller_id = #{sellerId}


	AND wb.serial_no = #{serialNo}


	AND wb.creator_id = #{operatorId}


	AND wb.created_time >=
	#{createdStart}


	AND wb.created_time <=
	#{createdEnd}


	AND wb.plate_number LIKE CONCAT('%',#{plateNumber},'%')


	AND wb.unit_price >=
	#{unitPriceStartValue}


	AND wb.unit_price <=
	#{unitPriceEndValue}


	AND ws.serial_no = #{wsSerialNo}


	AND wb.market_id = #{marketId}


	AND (wb.creator_id = #{operatorId} OR wb.modifier_id = #{operatorId})

ORDER BY ws.created_time DESC

SELECT

,

FROM
weighing_bill wb
INNER JOIN
weighing_statement ws
ON
wb.id =
ws.weighing_bill_id
WHERE 1 = 1

	AND wb.id > #{idStart}


	AND wb.state=#{state}


	AND wb.trade_type=#{tradeType}


	AND wb.goods_name IN
	
		#{goods}
	


	AND wb.buyer_id = #{buyerId}


	AND wb.seller_id = #{sellerId}


	AND wb.serial_no = #{serialNo}


	AND ws.serial_no = #{wsSerialNo}


	AND wb.creator_id = #{operatorId}


	AND wb.created_time >=
	#{createdStart}


	AND wb.created_time <=
	#{createdEnd}


	AND wb.modified_time>=
	#{modifiedStart}


	AND wb.modified_time <=
	#{modifiedEnd}


	AND wb.plate_number LIKE CONCAT('%',#{plateNumber},'%')


	AND wb.unit_price >=
	#{unitPriceStartValue}


	AND wb.unit_price <=
	#{unitPriceEndValue}


	AND (wb.price_state = 2 OR wb.price_state IS NULL)


	AND ws.state IN
	
		#{item}
	


	AND wb.creator_id = #{creatorId}


	AND wb.market_id = #{marketId}


	ORDER BY wb.settlement_time DESC


	ORDER BY wb.created_time DESC
