mapper.select:
SELECT DISTINCT
,wb.measure_type, wb.goods_name, wb.unit_amount, wb.unit_price, wb.unit_weight, wb.net_weight FROM weighing_statement ws INNER JOIN weighing_bill wb ON ws.weighing_bill_id = wb.id INNER JOIN weighing_bill_operation_record wbor ON wbor.statement_id = ws.id WHERE 1 =1
AND ws.state = #{state} AND wbor.operation_type = 4 AND ws.state = #{state} AND wbor.operation_type = 5 AND ws.state = #{state} AND wbor.operation_type = 2
AND wbor.operation_time >= #{startTime}
AND wbor.operation_time <= #{endTime}
AND (wb.buyer_account IN
#{accountId}
OR wb.seller_account IN
#{accountId}
)
ORDER BY ${sort} ${order}
ORDER BY wbor.operation_time DESC

SELECT ws.state, SUM( ws.frozen_amount ) AS total_frozen_amount, SUM( ws.trade_amount ) AS total_trade_amount, count(*) AS state_count FROM weighing_statement ws INNER JOIN weighing_bill wb ON ws.weighing_bill_id = wb.id INNER JOIN weighing_bill_operation_record wbor ON wbor.statement_id = ws.id AND (( ws.state = 2 AND wbor.operation_type = 4 ) OR ( ws.state = 3 AND wbor.operation_type = 5 ) OR ( ws.state = 4 AND wbor.operation_type = 2 )) WHERE 1 = 1 AND ws.state IN ( 2, 3, 4 )
AND wbor.operation_time >= #{startTime}
AND wbor.operation_time <= #{endTime}
AND (wb.buyer_account IN
#{accountId}
OR wb.seller_account IN
#{accountId}
)
GROUP BY ws.state

SELECT
DATE_FORMAT(last_operation_time,'%Y-%m-%d') AS 'time',
ROUND(SUM(ws.trade_amount+wb.staff_charges+wb.packing_charges+wb.collection_charges)/100,2) AS 'amount',
COUNT(ws.id) AS 'cnt',
GROUP_CONCAT(ws.id) AS 'ids'
FROM weighing_statement ws
INNER JOIN weighing_bill wb ON ws.weighing_bill_id=wb.id
WHERE 1 = 1

	AND ws.buyer_id = #{buyerId}


	AND ws.seller_id = #{sellerId}


	AND wb.buyer_card_account = #{accountBuyerId}


	AND wb.seller_card_account = #{accountSellerId}

AND ws.state = 2
AND market_id = #{marketId}

	AND ws.id IN
	(
	
		#{collectionRecordId}
	
	)


	AND wb.department_id IN
	
		#{departmentId}
	

GROUP BY DATE_FORMAT(last_operation_time,'%Y-%m-%d')
ORDER BY time ASC
